import React, { useState } from "react";
import "./reset.css";
import "./style.css"; // Import the CSS file
import { Switch } from "@mui/material";

function App() {
  // State for toggles
  const [darkMode, setDarkMode] = useState(false);
  const [dynamicBackground, setDynamicBackground] = useState(false);
  const [fontSize, setFontSize] = useState(false);
  const [temperatureUnit, setTemperatureUnit] = useState(false);

  return (
    <div className="container">
      <nav className="box sidebar">
        <div className="sidebarimage">
          <img src="/sidebaricon.png" alt="sidebar" />
        </div>
      </nav>

      <header className="box title">
        <div className="settingstext">
          <h1>Settings</h1>
        </div>
      </header>

      {/* Reusable Section Component */}
      {[
        { title: "Appearance" },
        { title: "Location Settings" },
        { title: "Notification Settings" },
        { title: "About and Support" },
      ].map((section, index) => (
        <section className="box appearance" key={index}>
          <div className="appearancetitle">
            <h1>{section.title}</h1>
          </div>
          <article className="settings">
            <h1>Dark Mode</h1>
            <Switch checked={darkMode} onChange={() => setDarkMode(!darkMode)} />
          </article>
          <article className="settings">
            <h1>Dynamic Background</h1>
            <Switch checked={dynamicBackground} onChange={() => setDynamicBackground(!dynamicBackground)} />
          </article>
          <article className="settings">
            <h1>Font Size</h1>
            <Switch checked={fontSize} onChange={() => setFontSize(!fontSize)} />
          </article>
          <article className="settings">
            <h1>Temperature Unit</h1>
            <Switch checked={temperatureUnit} onChange={() => setTemperatureUnit(!temperatureUnit)} />
          </article>
        </section>
      ))}
    </div>
  );
}

export default App;
