import React, { useState } from "react";
import "./reset.css";
import "./style.css"; // Import the CSS file
import { Switch, FormControlLabel } from "@mui/material";

function App() {
  // State for toggles
  const [darkMode, setDarkMode] = useState(false);
  const [dynamicBackground, setDynamicBackground] = useState(false);
  const [fontSize, setFontSize] = useState(false);
  const [temperatureUnit, setTemperatureUnit] = useState(false);
  const [locationMode, setLocationMode] = useState(false);

  return (
    <div className="container">
      <nav className="box sidebar">
        <div className="sidebarimage">
          <img src="/sidebaricon.png" alt="sidebar" />
        </div>
      </nav>

      <header className="box title">
        <div className="settingstext">
          <h1>Settings</h1>
        </div>
      </header>

      {/* Reusable Section Component */}
      {[{ title: "Appearance", items: [
        { label: "Dark Mode", state: darkMode, setState: setDarkMode },
        { label: "Dynamic Background", state: dynamicBackground, setState: setDynamicBackground },
        { label: "Font Size", state: fontSize, setState: setFontSize }
      ]},
      { title: "Location Settings", items: [
        { label: "Current Location", state: locationMode, setState: setLocationMode, labels: ["A", "M"] }
      ]},
      { title: "Notification Settings", items: [
        { label: "Weather Alerts", state: darkMode, setState: setDarkMode },
        { label: "Notify On Ideal Time To Train", state: dynamicBackground, setState: setDynamicBackground }
      ]},
      { title: "About and Support", items: [
        { label: "Temperature Unit", state: temperatureUnit, setState: setTemperatureUnit, labels: ["째C", "째F"] }
      ]}].map((section, index) => (
        <section className="box appearance" key={index}>
          <div className="appearancetitle">
            <h1>{section.title}</h1>
          </div>
          {section.items.map((item, idx) => (
            <article className="settings" key={idx}>
              <h1>{item.label}</h1>
              {item.labels ? (
                <FormControlLabel
                  control={<Switch checked={item.state} onChange={() => item.setState(!item.state)} />}
                  labelPlacement="start"
                  label={<span>{item.labels[0]} &nbsp;&nbsp; {item.labels[1]}</span>}
                />
              ) : (
                <Switch checked={item.state} onChange={() => item.setState(!item.state)} />
              )}
            </article>
          ))}
        </section>
      ))}
    </div>
  );
}

export default App;
