// Sidebar.js
import React from "react";

const Sidebar = ({ isOpen, toggleSidebar }) => {
  return (
    <div
      className={`fixed top-0 left-0 h-full w-64 bg-white shadow-lg transform ${
        isOpen ? "translate-x-0" : "-translate-x-full"
      } transition-transform duration-300 ease-in-out z-50`}
    >
      {/* Close Button */}
      <button className="absolute top-4 right-4 text-2xl" onClick={toggleSidebar}>
        ‚ùå
      </button>

      {/* Sidebar Content */}
      <div className="p-6 mt-10">
        <h2 className="text-2xl font-bold mb-4">Hi, John Doe</h2>
        <nav className="space-y-4">
          <a href="#" className="block text-lg">Home</a>
          <a href="#" className="block text-lg">Profile</a>
          <a href="#" className="block text-lg">My Sports</a>
          <a href="#" className="block text-lg">Settings</a>
        </nav>
      </div>
    </div>
  );
};

export default Sidebar;
