import React, { useState, useEffect } from "react";
import "./reset.css";
import "./style.css";
import { Switch, Select, MenuItem, TextField, Button } from "@mui/material";

function Settings() {
  // Load settings from localStorage or use defaults
  const getInitialSettings = () => {
    try {
      const savedSettings = localStorage.getItem('weatherAppSettings');
      return savedSettings ? JSON.parse(savedSettings) : {
        darkMode: false,
        dynamicBackground: false,
        fontSize: "Small",
        temperatureUnit: "celsius", // 'celsius' or 'kelvin'
        currentLocation: "A", // 'A' for Auto, 'M' for Manual
        manualLocation: "", // The manually entered location
        updateFrequency: 60 * 60 * 1000, // Default: 1 hour in milliseconds
        weatherAlerts: false,
        notifyTraining: false,
        dailyForecast: "Morning",
      };
    } catch (error) {
      console.error("Error loading settings:", error);
      return {
        darkMode: false,
        dynamicBackground: false,
        fontSize: "Small",
        temperatureUnit: "celsius",
        currentLocation: "A",
        manualLocation: "",
        updateFrequency: 60 * 60 * 1000,
        weatherAlerts: false,
        notifyTraining: false,
        dailyForecast: "Morning",
      };
    }
  };

  const [settings, setSettings] = useState(getInitialSettings);
  const [manualLocation, setManualLocation] = useState(settings.manualLocation || "");

  // Save settings to localStorage and broadcast changes
  const saveSettings = (newSettings) => {
    try {
      localStorage.setItem('weatherAppSettings', JSON.stringify(newSettings));

      // Dispatch a custom event to notify other components about the settings change
      window.dispatchEvent(new Event('settingsChanged'));
    } catch (error) {
      console.error("Error saving settings:", error);
    }
  };

  // Handle toggle switches
  const handleToggle = (settingKey) => {
    setSettings((prev) => {
      const newSettings = { ...prev };

      if (settingKey === "temperatureUnit") {
        // Toggle between celsius and kelvin
        newSettings.temperatureUnit = prev.temperatureUnit === "celsius" ? "kelvin" : "celsius";
      } else {
        // For boolean toggles
        newSettings[settingKey] = !prev[settingKey];
      }

      saveSettings(newSettings);
      return newSettings;
    });
  };

  // Handle dropdown selections
  const handleSelectChange = (event, settingKey) => {
    setSettings((prev) => {
      const newSettings = { ...prev };

      if (settingKey === "updateFrequency") {
        // Convert hours to milliseconds
        const hours = parseInt(event.target.value);
        newSettings.updateFrequency = hours * 60 * 60 * 1000;
      } else {
        newSettings[settingKey] = event.target.value;
      }

      saveSettings(newSettings);
      return newSettings;
    });
  };

  // Handle manual location setting
  const handleSetLocation = () => {
    if (manualLocation.trim()) {
      const newSettings = {
        ...settings,
        currentLocation: "M",
        manualLocation: manualLocation
      };

      setSettings(newSettings);
      saveSettings(newSettings);
    }
  };

  // Get display value for update frequency in hours
  const getUpdateFrequencyHours = () => {
    return settings.updateFrequency / (60 * 60 * 1000);
  };

  return (
    <div className="container">

      <header className="box title">
        <div className="settingstext">
          <h1>Settings</h1>
        </div>
      </header>

      <section className="box appearance">
        <div className="appearancetitle">
          <h1>Appearance</h1>
        </div>
        <article className="settings">
          <h1>Dark Mode</h1>
          <Switch checked={settings.darkMode} onChange={() => handleToggle("darkMode")} />
        </article>
        <article className="settings">
          <h1>Dynamic Background</h1>
          <Switch checked={settings.dynamicBackground} onChange={() => handleToggle("dynamicBackground")} />
        </article>
        <article className="settings">
          <h1>Font Size</h1>
          <Select 
            value={settings.fontSize} 
            onChange={(e) => handleSelectChange(e, "fontSize")}
            variant="standard" 
            disableUnderline
          >
            <MenuItem value="Small">Small</MenuItem>
            <MenuItem value="Medium">Medium</MenuItem>
            <MenuItem value="Large">Large</MenuItem>
          </Select>
        </article>
        <article className="settings">
          <h1>Temperature Unit</h1>
          <div className="unit-display">
            <span>{settings.temperatureUnit === "celsius" ? "°C" : "K"}</span>
            <Switch 
              checked={settings.temperatureUnit === "kelvin"} 
              onChange={() => handleToggle("temperatureUnit")} 
            />
          </div>
        </article>
      </section>

      <section className="box appearance">
        <div className="appearancetitle">
          <h1>Location Settings</h1>
        </div>
        <article className="settings">
          <h1>Current Location</h1>
          <Select 
            value={settings.currentLocation} 
            onChange={(e) => handleSelectChange(e, "currentLocation")}
            variant="standard" 
            disableUnderline
          >
            <MenuItem value="A">Auto</MenuItem>
            <MenuItem value="M">Manual</MenuItem>
          </Select>
        </article>
        <article className="settings">
          <h1>Set Location</h1>
          <div className="location-input">
            <TextField 
              value={manualLocation} 
              onChange={(e) => setManualLocation(e.target.value)} 
              variant="standard" 
              InputProps={{ disableUnderline: true }} 
              placeholder="Enter location"
              defaultValue={settings.manualLocation}
            />
            <Button 
              variant="contained" 
              size="small" 
              onClick={handleSetLocation}
              style={{ marginLeft: '10px' }}
            >
              Set
            </Button>
          </div>
        </article>
        <article className="settings">
          <h1>Update Frequency</h1>
          <Select 
            value={getUpdateFrequencyHours()} 
            onChange={(e) => handleSelectChange(e, "updateFrequency")}
            variant="standard" 
            disableUnderline
          >
            <MenuItem value={1}>1 Hour</MenuItem>
            <MenuItem value={3}>3 Hours</MenuItem>
            <MenuItem value={6}>6 Hours</MenuItem>
          </Select>
        </article>
      </section>

      <section className="box appearance">
        <div className="appearancetitle">
          <h1>About and Support</h1>
        </div>
        <article className="settings">
          <h1>App Version</h1>
          <h1>16.789.4.3</h1>
        </article>
        <article className="settings">
          <h1>Terms Of Service</h1>
        </article>
      </section>
    </div>
  );
}

export default Settings;
